	private void registerFont(String fontName, String nomeAssociado) throws PdfCreatorException {

		URL fontPath;
		try {
			fontPath = new URL(fontName);
			FontFactory.register(fontPath.toString(), nomeAssociado);
			System.out.println("SUCESSO no REGISTRO DA FONTE = " + fontName);
		} catch (Exception e) {
			logger.log(Level.SEVERE, "processaCabecalhoRodape = " + e);
			System.out.println("ERRO no REGISTRO DA FONTE = " + fontName);
			throw new PdfCreatorException(e.getMessage());
		}

	}

	/**
	 * Criando o PDF a partir do array de Bytes
	 * @param first
	 */
	private void criarPdfEmDisco(PdfExample first, String path) {
		try (FileOutputStream fileOutputStream = new FileOutputStream(path)) {
			//convert file into array of bytes
			byte[] arrayBytes = first.baos.toByteArray();

			fileOutputStream.write(arrayBytes);
		} catch (Exception e) {
			logger.log(Level.SEVERE, "criarPdfEmDisco = " + e);
		}
	}

	/**
	 * Gera o objeto documento baseado no A4. Define as margens de cabealho, rodap e margens esquerda e direita
	 * 
	 * pageSize the pageSize
	 * marginLeft the margin on the left
	 * marginRight the margin on the right
	 * marginTop the margin on the top ***
	 * marginBottom the margin on the bottom ***
	 * Document document = new Document(PageSize.A4, 85.35826771653F, 56.90551181102F, 500.90551181102F, 100.90551181102F);
	 * 
	 * A altura do cabealho vai diferena entre o primeiro e o terceiro parmetro.
	 * 
	 * A altura do rodap vai diferena entre o segundo e o quarto parmetro.
	 * 
	 * Os valores de incio so:
	 * 85.35826771653F, 56.90551181102F, 200, 130.90551181102F
	 * 
	 * document = new Document(PageSize.A4, 85.35826771653F, 56.90551181102F, 200, 130.90551181102F);
	 * 
	 * @param rodape
	 * @param cabecalho
	 * @param conteudo
	 * @param bExisteCabecalhoRodape
	 * @throws DocumentException
	 * @throws IOException
	 */
	private void prepararDocumento(String rodape, String cabecalho, String conteudo, boolean bExisteCabecalhoRodape) throws PdfCreatorException {
		Document document;

		try {
			if (this.modeloTemplateMPF) {
				document = new Document(PageSize.A4, IConstantes.DOCUMENT_MARGIN_LEFT, IConstantes.DOCUMENT_MARGIN_RIGHT, IConstantes.DOCUMENT_MARGIN_TOP_MODELO_MPF,
					IConstantes.DOCUMENT_MARGIN_BOTTOM);
			} else {
				document = new Document(PageSize.A4, IConstantes.DOCUMENT_MARGIN_LEFT, IConstantes.DOCUMENT_MARGIN_RIGHT, IConstantes.DOCUMENT_MARGIN_TOP_NOTEMPLATE,
					IConstantes.DOCUMENT_MARGIN_BOTTOM);
			}

			this.baos = new ByteArrayOutputStream();
			this.writer = PdfWriter.getInstance(document, this.baos);

			//Processador das tags HTML
			this.pdfProcessor = new PdfProcessor(this.writer);

			//Adicionando Header e Footer - disparando evento
			processaCabecalhoRodape(rodape, cabecalho, bExisteCabecalhoRodape);

			//processa o contedo principal
			processarConteudo(conteudo, document);

			//Para escrever a pginao no final da pgina
			escreverNumerosPaginas(document);

		} catch (Exception e) {
			throw new PdfCreatorException(e);
		}
	}

	/**
	 * Processamento do Cabealho e Rodap.
	 * 
	 * @param rodape
	 * @param cabecalho
	 * @param bExisteCabecalhoRodape
	 * @throws Exception
	 */
	private void processaCabecalhoRodape(String rodape, String cabecalho, boolean bExisteCabecalhoRodape) throws PdfCreatorException {
		try {
			if (bExisteCabecalhoRodape) {
				this.headerFooter = new HeaderFooter(cabecalho, rodape, this.pdfProcessor);
				this.headerFooter.setCabecalhoSomentePaginaUm(this.headerNoLoop);
				this.headerFooter.setRodapeSomentePaginaUm(this.footerNoLoop);

				//Processa o cabealho e rodap.
				this.writer.setPageEvent(headerFooter);
			}
		} catch (Exception e) {
			logger.log(Level.SEVERE, "processaCabecalhoRodape = " + e);
			throw new PdfCreatorException(e.getMessage());
		}
	}

	private void processarConteudo(String conteudo, Document document) throws DocumentException {
		if (!this.modeloTemplateMPF) {
			document.setMargins(40, IConstantes.DOCUMENT_MARGIN_RIGHT, IConstantes.DOCUMENT_MARGIN_TOP_NOTEMPLATE, IConstantes.DOCUMENT_MARGIN_BOTTOM_REDUCED);
			document.setMarginMirroring(false);
		}

		document.open();

		conteudoHmtl.add(conteudo);

		if (this.modeloTemplateMPF) {
			if (this.headerNoLoop) {
				this.processarConteudoCorpo(document, conteudoHmtl);
			} else {
				this.processarConteudoCorpoComRepeticaoCabecalho(document, conteudoHmtl);
			}
		} else {
			this.processarConteudoCorpo(document, conteudoHmtl);
		}

		document.close();
	}

	/**
	 * Este cdigo gera um novo array de bytes. O nmero de pginas so inseridos.
	 * @param document
	 * @throws Exception
	 */
	private void escreverNumerosPaginas(Document documentOriginal) throws Exception {
		Document document = documentOriginal;

		if (document == null) {
			document = new Document(PageSize.A4, 85.35826771653F, 56.90551181102F, 56.90551181102F, 56.90551181102F);
		}

		document.open();
		if (this.headerFooter != null) {
			this.baos = this.headerFooter.escreverNumerosDePagina(this.baos.toByteArray());
		} else {
			HeaderFooter tmp = new HeaderFooter();

			if (this.baos != null) {
				this.baos = tmp.escreverNumerosDePagina(this.baos.toByteArray());
			} else {
				this.baos = tmp.escreverNumerosDePagina(this.bytes);
			}
		}
		document.close();
	}

	/**
	 * Adiciona o contedo do HTML.Este contedo vai para a rea central do documento.
	 * 
	 * Neste caso a margem do cabealho  maior porque ele vem preenchido com imagem e outras informaes.
	 * 
	 * @param document
	 * @param pagesHmtl
	 */
	private void processarConteudoCorpoComRepeticaoCabecalho(Document document, List<String> pagesHmtl) {
		if (pagesHmtl == null) {
			return;
		}

		float tamRodape = IConstantes.DOCUMENT_MARGIN_BOTTOM;

		if (this.rodapeInexistente) {
			tamRodape = IConstantes.DOCUMENT_MARGIN_BOTTOM_REDUCED;
		}

		for (String page : pagesHmtl) {
			try {
				for (Element e : this.pdfProcessor.parseToElementList(page, null)) {
					document.setMargins(IConstantes.DOCUMENT_MARGIN_LEFT, IConstantes.DOCUMENT_MARGIN_RIGHT, IConstantes.DOCUMENT_MARGIN_TOP_MODELO_MPF, tamRodape);
					document.add(e);
				}
			} catch (Exception e) {
				logger.log(Level.SEVERE, "processarConteudoCorpoComRepeticaoCabecalho = " + e);
			}
		}

	}

	/**
	 * Adiciona o contedo do HTML.Este contedo vai para a rea central do documento.
	 * 
	 * Neste caso a margem do cabealho  menor porque no vai existir nenhum contedo no cabealho.
	 * 
	 * @param document
	 * @param pagesHmtl
	 * @throws DocumentException
	 */
	private void processarConteudoCorpo(Document document, java.util.List<String> pagesHmtl) throws DocumentException {
		if (pagesHmtl == null) {
			return;
		}

		float tamRodape = IConstantes.DOCUMENT_MARGIN_BOTTOM;

		if (this.rodapeInexistente) {
			tamRodape = IConstantes.DOCUMENT_MARGIN_BOTTOM_REDUCED;
		}

		for (String page : pagesHmtl) {
			try {
				for (Element e : this.pdfProcessor.parseToElementList(page, null)) {
					document.setMargins(IConstantes.DOCUMENT_MARGIN_LEFT, IConstantes.DOCUMENT_MARGIN_RIGHT, IConstantes.DOCUMENT_MARGIN_TOP_REDUCED, tamRodape);
					document.add(e);
				}
			} catch (Exception e) {
				logger.log(Level.SEVERE, "processarConteudoCorpo = " + e);
			}
		}
	}

